    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Log in!{% endblock %}
{% block stylesheets %}
        <link rel=\"stylesheet\" href=\" {{ asset(\"css/login.css\")}} \">

{% endblock %}

{% block body %}
    <h1 class=\"h3 mb-3 font-weight-normal\">Please sign in</h1>
<div id=\"login_form_holder\">
<form id=\"login_form\" method=\"post\">
    {% if error %}
        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    {% if app.user %}
        <div class=\"mb-3\">
            You are logged in as {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">Logout</a>
        </div>
    {% endif %}



    <label for=\"inputUsername\">Username</label>
    <select value=\"{{ last_username }}\" name=\"username\" id=\"inputUsername\" required autofocus>
        <option value=\"\">--Please select user--</option>
        {% for user in users %}
            <option value=\"{{ user.username }}\">{{ user.username }}</option>
        {% endfor %}
    </select>

    {# <input type=\"text\" value=\"{{ last_username }}\" name=\"username\" id=\"inputUsername\" required autofocus> #}
    
    <label for=\"inputPassword\">Password</label>
    <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" required>

    <input type=\"hidden\" name=\"_csrf_token\"
           value=\"{{ csrf_token('authenticate') }}\"
    >

    {#
        Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
        See https://symfony.com/doc/current/security/remember_me.html

        <div class=\"checkbox mb-3\">
            <label>
                <input type=\"checkbox\" name=\"_remember_me\"> Remember me
            </label>
        </div>
    #}

    <button class=\"btn btn-lg btn-primary\" type=\"submit\">
        Sign in
    </button>
</form>
</div>
{% endblock %}

{% block footer %}
    <a href=\"{{ path('app_home') }}\" class=\"btn btn-back\">Back</a>
{% endblock %}
", "security/login.html.twig", "/Users/sebastiendumont/Documents/Projets/restock/restockApp/templates/security/login.html.twig");
    }
